'use strict';
const Metric = require('./index');
const Util = require('./util');

module.exports = class extends Metric {

    add(key, value, date) {

        key = Util.getKey(key);
        this.process(date);
        this.currentValue[key] = this.currentValue[key] || [];
        this.currentValue[key].push(value);
    }

};
