'use strict';
const Metrics = require('./index');

module.exports.enableCallCount = function (period) {

    Metrics.getMetric({
        kind: 'Sum',
        name: 'sqreen_call_counts',
        period: period || 60
    });
};

module.exports.enableWhitelisted = function () {

    Metrics.getMetric({
        kind: 'Sum',
        name: 'whitelisted',
        period: 60
    });
};
