'use strict';
const Code = require('code');
const Lab = require('lab');
const lab = exports.lab = Lab.script();

const describe = lab.describe;
const it = lab.it;
const expect = Code.expect;

const Proxyquire = require('proxyquire');


describe('Hook', () => {

    describe('jade', () => {

        it('should run the hook', { plan: 2 }, (done) => {

            const Hook = Proxyquire('../../../../lib/instrumentation/hooks/jadeHook', {
                '../../rules': {
                    enforceRuleList: function (rule, nosign) {

                        expect(nosign).to.be.true();
                        expect(rule[0].name).to.equal('xss_jade');
                        done();
                    }
                }
            });

            Hook();
        });
    });
});
