'use strict';
const Lab = require('lab');
const lab = exports.lab = Lab.script();

const describe = lab.describe;
const it = lab.it;

const Logger = require('../../lib/logger');

describe('logger', () => {

    it('should log a WARN message', { plan: 0 }, (done) => {

        Logger.WARN('hello');
        done();
    });

    it('should log a WARN message without content', { plan: 0 }, (done) => {

        Logger.WARN();
        done();
    });
});

