'use strict';
const Code = require('code');
const Lab = require('lab');
const lab = exports.lab = Lab.script();
const beforeEach = lab.beforeEach;

const describe = lab.describe;
const it = lab.it;
const expect = Code.expect;

const Metric = require('../../../lib/metric');
const Sum = require('../../../lib/metric/collect');

describe('Metric', () => {

    beforeEach((done) => {

        Metric._clearAllMetrics();
        done();
    });

    describe('Collect', () => {

        it('should create a collect metric', { plan: 2, timeout: 20000 }, (done) => {

            const collect = new Sum({
                kind: 'Collect',
                name: 'login-fail',
                period: 2
            });

            collect.add('key1', 1);
            collect.add('key2', 1);

            expect(Metric.getMetricByName('login-fail')).to.equal(collect);
            setTimeout(() => {

                collect.add('key1', 1);
                collect.add('key1', 1);
                collect.add('key3', 1);
            }, 3000);

            setTimeout(() => {

                collect.process();
                expect(collect.values.map((x) => x.value)).to.equal([{ 'key1': [1], 'key2': [1] }, { 'key1': [1, 1], 'key3': [1] }]);
                done();
            }, 6000);

        });
    });
});
