'use strict';
const Code = require('code');
const Lab = require('lab');
const lab = exports.lab = Lab.script();

const describe = lab.describe;
const it = lab.it;
const expect = Code.expect;

const Check = require('../versionCheck');

describe('versionCheck', () => {

    it('should test it', { plan: 8 }, (done) => {

        expect(Check('v0.12.15')).to.be.false();
        expect(Check('v0.1f')).to.be.false();
        expect(Check('v3.0')).to.be.false();
        expect(Check('v4.0.0')).to.be.true();
        expect(Check('v5.0.0')).to.be.true();
        expect(Check('v6.0.0')).to.be.true();
        expect(Check('v7.0.0')).to.be.true();
        expect(Check('8.0.0')).to.be.true();
        done();
    });
});
